import { StatusBar } from "expo-status-bar";
import { NavigationContainer } from "@react-navigation/native";
import { createNativeStackNavigator } from "@react-navigation/native-stack";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import { createDrawerNavigator } from "@react-navigation/drawer";
import { Ionicons } from "@expo/vector-icons";
import { UserProvider } from "./src/context/UserContext";

import HomeScreen from "./src/screens/HomeScreen";
import UserScreen from "./src/screens/UserScreen";
import SettingScreen from "./src/screens/SettingScreen";
import CategoriesScreen from "./src/screens/CategoriesScreen";
import LoginScreen from "./src/screens/LoginScreen";
//import PostScreen from "./src/screens/PostScreen";
import SearchScreen from "./src/screens/SearchScreen";

import AddAdvert from "./src/screens/AddAdvert";
import AdvertDetails from "./src/screens/AdvertDetails";
import CategoryDetails from "./src/screens/CategoryDetails";
import EditProfile from "./src/screens/EditProfile";
import MessageDetails from "./src/screens/MessageDetails";
import MessageList from "./src/screens/MessageList";
//import Post from "./src/screens/PostScreen";
import AddCompany from "./src/screens/AddCompany";
import EditCompany from "./src/screens/EditCompany";
import React from "react";
import { Settings, StyleSheet, Text, View } from "react-native";
import PostScreen from "./src/screens/PostScreen";
import ImageBrowserScreen from "./src/screens/ImageBrowserScreen";
import { backgroundColor } from "react-native/Libraries/Components/View/ReactNativeStyleAttributes";

const HomeStack = createNativeStackNavigator();
function HomeStackScreen() {
  return (
    <HomeStack.Navigator>
      <HomeStack.Screen
        name="Home"
        component={HomeScreen}
        options={{ headerShown: false }}
      />
      <HomeStack.Screen name="LoginPage" component={LoginScreen} />
      <HomeStack.Screen
        name="search"
        component={SearchScreen}
        options={{ headerShown: false }}
      />
      <CategoriesStack.Screen
        name="Category_details"
        component={CategoryDetails}
      />
      <CategoriesStack.Screen name="Advert_details" component={AdvertDetails} />
    </HomeStack.Navigator>
  );
}

const CategoriesStack = createNativeStackNavigator();

function CategoriesStackScreen() {
  return (
    <CategoriesStack.Navigator>
      <CategoriesStack.Screen name="Categories" component={CategoriesScreen} />
      <CategoriesStack.Screen
        name="Category_details"
        component={CategoryDetails}
      />
      <CategoriesStack.Screen name="Advert_details" component={AdvertDetails} />
    </CategoriesStack.Navigator>
  );
}

const AccountStack = createNativeStackNavigator();

function AccountStackScreen() {
  return (
    <AccountStack.Navigator
      screenOptions={{
        headerStyle: { backgroundColor: "#BD3737" },
        headerTintColor: "#fff",
      }}
    >
      <AccountStack.Screen
        name="Account"
        component={UserScreen}
        options={{ headerShown: false }}
      />
      <AccountStack.Screen
        name="Edit_profile"
        component={EditProfile}
        options={{
          title: "Edit Profile",
          headerStyle: { backgroundColor: "white" },
          headerTintColor: "#BD3737",
        }}
      />
      <AccountStack.Screen name="Add_advert" component={AddAdvert} />
      <AccountStack.Screen name="Advert_details" component={AdvertDetails} />
      <AccountStack.Screen name="Messages_list" component={MessageList} />
      <AccountStack.Screen name="Message_details" component={MessageDetails} />
      <AccountStack.Screen name="Post" component={PostScreen} />
      <AccountStack.Screen name="ImageBrowser" component={ImageBrowserScreen} />
    </AccountStack.Navigator>
  );
}

const SettingsStack = createNativeStackNavigator();

function SettingsStackScreen() {
  return (
    <SettingsStack.Navigator>
      <SettingsStack.Screen name="Settings" component={SettingScreen} />
    </SettingsStack.Navigator>
  );
}

const Tab = createBottomTabNavigator();
const Stack = createNativeStackNavigator();

function HomeTabs() {
  return (
    <Tab.Navigator
      screenOptions={{
        headerShown: false,
        tabBarShowLabel: true,
        tabBarActiveTintColor: "#d93232",
        tabBarStyle: {
          height: 50,
          paddingHorizontal: 2,
          paddingTop: 0,

          position: "absolute",
          borderTopWidth: 0,
          backgroundColor: "#eaebef",
        },
      }}
    >
      <Tab.Screen
        name="tab_Home"
        component={HomeScreen}
        options={{
          tabBarLabel: "Home",
          tabBarIcon: (tabInfo) => {
            return (
              <Ionicons
                name="home"
                size={20}
                color={tabInfo.focused ? "#BD3737" : "#8e8e93"}
              />
            );
          },
          headerShown: false,
        }}
      />
      <Tab.Screen
        name="Categories"
        component={CategoriesScreen}
        options={{
          tabBarLabel: "Categories",
          tabBarIcon: (tabInfo) => {
            return (
              <Ionicons
                name="list"
                size={20}
                color={tabInfo.focused ? "#BD3737" : "#8e8e93"}
              />
            );
          },
        }}
      />
      <Tab.Screen
        name="Account"
        component={UserScreen}
        options={{
          tabBarLabel: "Account",
          tabBarIcon: (tabInfo) => {
            return (
              <Ionicons
                name="person"
                size={20}
                color={tabInfo.focused ? "#BD3737" : "#8e8e93"}
              />
            );
          },
        }}
      />
      <Tab.Screen
        name="Settings"
        component={SettingScreen}
        options={{
          tabBarLabel: "Settings",
          tabBarIcon: (tabInfo) => {
            return (
              <Ionicons
                name="settings"
                size={20}
                color={tabInfo.focused ? "#BD3737" : "#8e8e93"}
              />
            );
          },
        }}
      />
    </Tab.Navigator>
  );
}
function App(navigation) {
  return (
    <NavigationContainer>
      <Stack.Navigator>
        <Stack.Screen name="Home" component={HomeTabs} options={{headerShown:false}}/>
        <Stack.Screen name="LoginPage" component={LoginScreen} />
        <Stack.Screen
          name="search"
          component={SearchScreen}
          options={{ headerShown: false }}
        />
        <Stack.Screen name="Category_details" component={CategoryDetails} options={{ headerShown: false }}/>

        <Stack.Screen
          name="Edit_profile"
          component={EditProfile}
          options={{
            title: "Edit Profile",
            headerStyle: { backgroundColor: "#fff",headerTintColor:"#000" },

          }}
        />
        <Stack.Screen name="Add_advert" component={AddAdvert} />
        <Stack.Screen name="Advert_details" component={AdvertDetails} />
        <Stack.Screen name="Messages_list" component={MessageList} />
        <Stack.Screen name="Message_details" component={MessageDetails} />
        <Stack.Screen name="Post" component={PostScreen} options={{
          headerStyle:{backgroundColor:'#BD3737'}, headerTintColor:'#fff'
          }}/>
          <Stack.Screen name="Add_company" component={AddCompany} options={{
         title:'Add Company / Business'
          }}/>
           <Stack.Screen name="Edit_company" component={EditCompany} options={{
         title:'Edit Company / Business'
          }}/>
        <Stack.Screen name="ImageBrowser" component={ImageBrowserScreen} />
      </Stack.Navigator>
    </NavigationContainer>
  );
}

export default () => {
  return (
    <UserProvider>
      <App />
    </UserProvider>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },
});
