export const initialState = 
    {
        user_id:0,
        user_name:'',
        login_status:false,
    }

export const reducer=(state,{type,payload})=>
{
    switch(type)
    {
        case "LOGIN":
        return {user_id:payload.user_id,user_name:payload.user_name,login_status:false}
        
        case "LOGOUT":
        return {user_id:0,user_name:"",login_status:false}
        default:
        return state;
    }
    
    
  
}
