import React, { Component} from 'react';
import {Image,ActivityIndicator,FlatList,Text, View, Animated,SafeAreaView,ScrollView,TextInput,StyleSheet,StatusBar,Button} from "react-native";
import { Ionicons } from "@expo/vector-icons";
import LoginScreen from './LoginScreen';



const HEADER_MAX_HEIGHT = 140;
const HEADER_MIN_HEIGHT = 50;

class HomeSreen extends Component
{

  constructor(props)
  {
    super(props);
    const { navigate } = this.props.navigation;
    //const {route}=this.props.route;
    //const {route}=this.props.route;
    this.state={
      scrollY:new Animated.Value(0),
      user_id:'0',
      login_status:false,

      isLoading:true,
      isLoading_two:true,
      category_one:[],
      category_two:[],
     
    }
  }
  componentDidMount()
  {
    fetch('https://app.merrytimesacademy.com/jengo/get_data.php',
    {
      method:'POST',
      headers:{
        'Accept':'application/json',
        'Content-Type':'application/json',
      },
      body:JSON.stringify(
        {
           data_request:'first_category_list'
        }
      )

    })
    .then((response)=> response.json())
    .then((responseJson) => {
        
      
        this.setState({category_one :responseJson})
        //this.setState({user_id:1})
    })
    .catch((error)=> 
    {
      alert(error)
    })
    .finally(()=> this.setState({isLoading:false}))

    fetch('https://app.merrytimesacademy.com/jengo/get_data_two.php',
    {
      method:'POST',
      headers:{
        'Accept':'application/json',
        'Content-Type':'application/json',
      },
      body:JSON.stringify(
        {
           data_request:'second_category_list'
        }
      )

    })
    .then((response)=> response.json())
    .then((responseJson) => {
        
      
        this.setState({category_two :responseJson})
        //this.setState({user_id:1})
    })
    .catch((error)=> 
    {
      alert(error)
    })
    .finally(()=> this.setState({isLoading_two:false}));
  }

  displayItems=(({item}) => 
    {
     return(
       <View style={{flex:1,margin:1, padding:2}}>
         <View style={{flex:1, justifyContent:'center', alignItems:'center',width:100,height:50,padding:2, marginBottom:3}}>
           <Image
              style={{width:50,height:50,margin:1}}
              source={{uri:'https://funzasoft.grahopesolutions.com/jengo/images/categories/'+item.image}}
           />
         </View>
         <View style={{flex:1, justifyContent:'center', alignItems:'center',paddingHorizontal:5,width:100,height:50}}>
              <Text  style={{fontSize:12,color:'#000',marginBottom:5,textAlign:'center'}}>
                {item.sub_category_name}
              </Text>
         </View>
      </View>
      
     )
    })

  renderSeparator=()=>{
    return(
      <View style={{width:'100%', height: 1,backgroundColor:'#ccc'}}></View>
    )
  }

  render()
  {
    const headerHeight=this.state.scrollY.interpolate({
      inputRange:[0,HEADER_MAX_HEIGHT-HEADER_MIN_HEIGHT],
      outputRange:[HEADER_MAX_HEIGHT,HEADER_MIN_HEIGHT], 
      extrapolate:'clamp'});

    return(
   <View style={{flex:1,backgroundColor:'#d93232'}}>
     
    <View style={{flex:1,marginTop:StatusBar.currentHeight, backgroundColor:'#d93232'}}>
       
        <Animated.View style={{top:0,left:0,height:0,height:HEADER_MAX_HEIGHT,backgroundColor:"#d93232"}}>
           <View style={{flex:1, justifyContent:'center', alignItems:'center'}}>
             <Text style={{color:'#fff',fontSize:20}}>Find Construction Materials</Text>
          </View>
          <View style={{flex:1}}>
           <View style={styles.searchSection}>
             <Ionicons style={styles.searchIcon} name="search" size={25} color="#000"/>
              <TextInput placeholder="Type your search here" style={styles.input} onFocus={()=> this.props.navigation.navigate('search')}/>
              <Ionicons style={styles.optionIcon} name="options" size={27} color="#000"/>
            </View>
          </View>
        </Animated.View>
       
    <ScrollView nestedScrollEnabled={true} 
    
    style={{ flex: 1,backgroundColor:'#fff',bottom:0}}>
     
     <View style={{width:'100%', marginBottom:4,marginTop:10,padding:5}}>
       <Text style={{fontWeight:'600',fontSize:16,width:'100%',borderBottomColor:'#d93232',  borderBottomWidth:2,}}>Construction Categories</Text>
     </View>
    
       
      <View style={{height:100, marginTop: 10}}>
      { this.state.isLoading 
       ? 
       <View style={{flex:1,justifyContent:'center', alignItems:'center'}}>
       <ActivityIndicator size="large" color="#d93232" animating/>
       </View> 
       : 
       null} 
      <FlatList 
          data={this.state.category_one}
          renderItem={this.displayItems}
          keyExtractor={item=> item.entry_id}
          //ItemSeparatorComponent={this.renderSeparator}
          horizontal
          
         
        
      />
      <View style={{height:1,backgroundColor:'#ccc',margin:5}}></View>
      </View>
      <View style={{width:'100%', marginBottom:4,marginTop:10,padding:5}}>
        <Text style={{ fontWeight:'600',fontSize:16,width:'100%',borderBottomColor:'#d93232',  borderBottomWidth:2,}}>Service Categories</Text>
      </View>
      <View style={{height:100, marginTop: 10}}>
      { this.state.isLoading_two 
       ? 
       <View style={{flex:1,justifyContent:'center', alignItems:'center'}}>
       <ActivityIndicator size="large" color="#d93232" animating/>
       </View> 
       : 
       null} 
      <FlatList 
          data={this.state.category_two }
          renderItem={this.displayItems}
          keyExtractor={item=> item.entry_id}
          //ItemSeparatorComponent={this.renderSeparator}
          horizontal
          
         
        
      />
      <View style={{height:1,backgroundColor:'#ccc',margin:5}}></View>
      </View>
    </ScrollView>
    </View>
    </View>
    );
  }
}
export default HomeSreen;

const styles=StyleSheet.create(
{
  input: {
    height: 45,
    margin: 10,
    width:'75%',
    paddingLeft:10,
    backgroundColor:'#fff',
    borderRadius:5,
    borderColor:'#ccc',
    color:'#000',
    fontSize:15,
    
    
  },
  searchSection: {
    flex: 1,
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#fff',
    margin:10,
    
    borderRadius:5,
    shadowColor: "#000",
    shadowOffset: {
   	  width: 0,
	   height: 3,
    },
    shadowOpacity: 0.27,
    shadowRadius: 4.65,

    elevation: 6,
   },
   searchIcon: {
    padding: 10,
    position: 'absolute',
    left:0,
    color:'#a0a1a1'
},
optionIcon: {
  padding: 10,
  position: 'absolute',
  right:0,
  color:'#d93232',
},
}
)
